//VERSION=3

// This custom script visualises raw FAPAR values

const map = [
  [0.0, 0xffffe5],
  [0.125, 0xf7fcb9],
  [0.25, 0xd9f0a3],
  [0.375, 0xaddd8e],
  [0.5, 0x78c679],
  [0.625, 0x41ab5d],
  [0.75, 0x238443],
  [0.875, 0x006837],
  [1.0, 0x004529],
];

const visualizer = new ColorMapVisualizer(map);

function setup() {
  return {
    input: ["FAPAR", "dataMask"],
    output: [
      { id: "default", bands: 4 },
      { id: "eobrowserStats", bands: 1 },
      { id: "dataMask", bands: 1 },
    ],
  };
}

function evaluatePixel(sample) {
  const val = sample.FAPAR * 0.0001;
  const imgVals = [...visualizer.process(val), sample.dataMask];

  return {
    default: imgVals,
    eobrowserStats: [val],
    dataMask: [sample.dataMask],
  };
}

