//VERSION=3

// This custom script visualises raw NDVI values

const map = [
  [-1, 0xffffff],
  [-0.5, 0xfefef3],
  [0.0, 0xffffe5],
  [0.1, 0xf7fcb9],
  [0.2, 0xd9f0a3],
  [0.35, 0xaddd8e],
  [0.5, 0x78c679],
  [0.65, 0x41ab5d],
  [0.8, 0x238443],
  [0.9, 0x006837],
  [1.0, 0x004529],
];
const visualizer = new ColorMapVisualizer(map);

function setup() {
  return {
    input: ["NDVI", "dataMask"],
    output: [
      { id: "default", bands: 4 },
      { id: "eobrowserStats", bands: 1 },
      { id: "dataMask", bands: 1 },
    ],
  };
}

//EvaluatePixel function
function evaluatePixel(sample) {
  var ndvi = (((sample.NDVI & 0xffff) << 16) >> 16) * 0.0001;
  const imgVals = [...visualizer.process(ndvi), sample.dataMask];

  return {
    default: imgVals,
    eobrowserStats: [ndvi],
    dataMask: [sample.dataMask],
  };
}

