//VERSION=3
const blue_red = [
  [273, 0xffffff],
  [274, 0xfefce7],
  [283, 0xfde191],
  [293, 0xf69855],
  [303, 0xec6927],
  [323, 0xaa2d1d],
  [363, 0x650401],
  [473, 0x3d0200],
];

const viz = new ColorRampVisualizer(blue_red);

function setup() {
  return {
    input: [
      {
        bands: ["F1", "dataMask"],
      },
    ],
    output: [
      { id: "default", bands: 4 },
      { id: "eobrowserStats", bands: 1, sampleType: 'FLOAT32' },
      { id: "dataMask", bands: 1 },
    ],
  };
}

function evaluatePixel(samples) {
  let val = samples.F1;
  val = viz.process(val);
  val.push(samples.dataMask);
  return {
    default: val,
    eobrowserStats: [samples.F1],
    dataMask: [samples.dataMask],
  };
}

