//VERSION=3

function setup() {
  return {
    input: ["GF", "dataMask"],
    output: { bands: 4, sampleType: "AUTO" },
  };
}

const ramps = [
  [0, 0xffffff],
  [100, 0x4d4dff],
];

const visualizer = new ColorRampVisualizer(ramps);

function evaluatePixel(sample) {
  var GF_viz = visualizer.process(sample.GF);
  if (sample.GF == 255) {
    return [0, 0, 0, sample.dataMask];
  } else if (sample.GF == 205) {
    return [0.9, 0.9, 0.9, sample.dataMask];
  } else {
    return [GF_viz[0], GF_viz[1], GF_viz[2], sample.dataMask];
  }
}

//VERSION=3

function setup() {
  return {
    input: ["QC", "dataMask"],
    output: { bands: 4, sampleType: "AUTO" },
  };
}
const map = [
  [0, 0x00a600], //0 - High Quality
  [1, 0xa6e64d], //1 - Medium Quality
  [2, 0xf2a64d], //2 - Low Quality
  [3, 0xff0000], //3 - Minimal Quality
  [250, 0xcccccc], //250 - Masked
  [255, 0x000000], //255 - NODATA
];

const visualizer = new ColorMapVisualizer(map);

function evaluatePixel(sample) {
  return [
    visualizer.process(sample.QC)[0],
    visualizer.process(sample.QC)[1],
    visualizer.process(sample.QC)[2],
    sample.dataMask,
  ];
}
