	const g = samples.B08*0.3 + samples.B03*2.5 + (samples.B03*1.0+ samples.B12*0.3);
    const b = samples.B08*0.3 + samples.B02*2.5 + (samples.B02*1.0+ samples.B12*0.3);
   return [r, g, b, samples.dataMask];
