//VERSION=3

function setup() {
  return {
    input: ["WSM", "dataMask"],
    output: { bands: 4, sampleType: "AUTO" },
  };
}
const map = [
  [110, 0xff4dff], //110 - Wet Snow
  [125, 0xffffff], //115 - Dry Snow or Snow free or patchy snow
  [200, 0x000000], //200 - Radar Shadow
  [210, 0x4d4dff], //210 - Water
  [220, 0x00a600], //220 - Forest
  [230, 0xf2a64d], //230 - Urban Areas
  [240, 0xccffcc], //240 - Non-mountain areas
  [255, 0xff0000], //255 - NODATA
];

const visualizer = new ColorMapVisualizer(map);

function evaluatePixel(sample) {
  return [
    visualizer.process(sample.WSM)[0],
    visualizer.process(sample.WSM)[1],
    visualizer.process(sample.WSM)[2],
    sample.dataMask,
  ];
}

//VERSION=3

function setup() {
  return {
    input: ["QCWSM", "dataMask"],
    output: { bands: 4, sampleType: "AUTO" },
  };
}
const map = [
  [0, 0x00a600], //0 - High Quality
  [1, 0xa6e64d], //1 - Medium Quality
  [2, 0xf2a64d], //2 - Low Quality
  [3, 0xff0000], //3 - Minimal Quality
  [250, 0xcccccc], //250 - Masked
  [255, 0x000000], //255 - NODATA
];

const visualizer = new ColorMapVisualizer(map);

function evaluatePixel(sample) {
  return [
    visualizer.process(sample.QCWSM)[0],
    visualizer.process(sample.QCWSM)[1],
    visualizer.process(sample.QCWSM)[2],
    sample.dataMask,
  ];
}
